import React, { useState } from 'react';
import './style.css';

export default function App() {
  const [data, setData] = useState([
    {
      id: 1,
      name: "vinnu",
    },
    {
      id: 2,
      name: "Babbi",
    },
    {
      id: 3,
      name: "Sam",
    },
  ]);
  const [list,setList]=useState()
  const [editid, setEditid] = useState(null);
  const [editvalue, setEditvalue] = useState("");

  const add=(e)=>{
   setData([...data,{name:list}])
  }

  const edit = (id, name) => {
    setEditid(id);
    setEditvalue(name);
  };

  const update = () => {
    if (editvalue.trim() !== "") {
      const update = data.map((item) =>
        item.id == editid ? { ...item, name: editvalue } : item
      );
      setdata(update);
      setEditid(null);
      setEditvalue("");
    }
  };
const del=(i)=>{
  const updatedData=data.filter((item)=>item.id!==i)
  setData(updatedData)
}
  return (
    <div>
      <h1>Hello StackBlitz!</h1>
      <input
        type="text"
        value={editid !== null ? editvalue : list}
        onChange={(e) => {
          editid !== null
            ? setEditvalue(e.target.value)
            : setList(e.target.value);
        }}
      />
      {editid !== null ? (
        <button onClick={update}>UPDATE</button>
      ) : (
        <button onClick={add}>ADD</button>
      )}
      {data.map((dta, i) => {
        return (
          <>
            <h3>
              id={dta.id}&nbsp;&nbsp;&nbsp;Name:{dta.name}
            </h3>{" "}
            <button onClick={() => del(dta.id)}>Delete</button>
            <button onClick={() => edit(dta.id, res.name)}>EDIT</button>
          </>
        );
      })}
    </div>
  );
}
