import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import Button from "react-bootstrap/Button";
function StdDetails() {
  const [data, setData] = useState({});
  const { stdDta } = useParams();
  useEffect(() => {
    fetch(`http://localhost:5000/data/${stdDta}`)
      .then((res) => res.json())
      .then((opt) => setData(opt));
  }, []);
  return (
    <>
      <h2>
        <u>Student Details</u>
      </h2>
      {
        <div className="detailspage">
          <h3>
            Student Id:<span>{data._id}</span>
          </h3>
          <h3>
            Student Name:<span>{data.name}</span>
          </h3>
          <h3>
            Student Email:<span>{data.email}</span>
          </h3>
          <h3>
            Student Age:<span>{data.age}</span>
          </h3>
          <h3>
            Student Percentage:<span>{data.perc}</span>
          </h3>
          <Link to="/">
            <Button variant="primary">{">>"}Back</Button>
          </Link>
        </div>
      }
    </>
  );
}
export default StdDetails;
