import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
function StdList() {
  const [data, setData] = useState([]);

  const navigate = useNavigate();
  useEffect(() => {
    getrecord();
  }, []);

  const getrecord = () => {
    fetch("http://localhost:5000/getdata")
      .then((res) => res.json())
      .then((op) => setData(op));
  };

  const Edit = (id) => {
    navigate("/edit/" + id);
  };
  const Details = (id) => {
    navigate("/details/" + id);
  };

  const Delete = (id) => {
    if (window.confirm("Do You Want To Remove Student data?")) {
      fetch(`http://localhost:5000/deletedata/${id}`, {
        method: "DELETE",
      })
        .then((res) => {
          alert("data removed successfully");
          getrecord();
          // window.location.reload()
        })
        .catch((error) => {
          console.log(error);
        });
    }
  };
  return (
    <>
      
        <h3>
          <u>-:Student Data:-</u>
        </h3>
        <h4>
          Add Student Data:
          <Link to="/create">
            <Button as="input" variant="info" type="button" value="ADD" />
          </Link>
        </h4>
        <hr />
        <Table striped bordered hover variant="dark">
          <thead>
            <tr>
              <th>S.no</th>
              <th>Name</th>
              <th>Age</th>
              <th>Percentage</th>
              <th colSpan="3">Additional</th>
            </tr>
          </thead>

          {data.map((dta, id) => {
            return (
              <>
                <tbody>
                  <tr>
                    <td>{id + 1}</td>
                    <td>{dta.name}</td>
                    <td>{dta.age}</td>
                    <td>{dta.perc}</td>

                    <td>
                      <Button
                        variant="primary"
                        as="input"
                        type="button"
                        value="Edit"
                        onClick={() => Edit(dta._id)}
                      />
                    </td>
                    <td>
                      <Button
                        variant="danger"
                        as="input"
                        type="button"
                        value="Details"
                        onClick={() => Details(dta._id)}
                      />
                    </td>
                    <td>
                      <Button
                        as="input"
                        variant="success"
                        type="button"
                        value="Delete"
                        onClick={() => Delete(dta._id)}
                      />
                    </td>
                  </tr>
                </tbody>
              </>
            );
          })}
        </Table>
      
    </>
  );
}
export default StdList;
