import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import Button from "react-bootstrap/Button";
import { Card, Container, Row } from "react-bootstrap";
import axios from "axios";
function StdDetails() {
  const [data, setData] = useState({});
  const { proDta } = useParams();
  useEffect(() => {
    axios
      .get(`http://localhost:5000/data/${proDta}`)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [proDta]); // Include proDta in the dependency array if you want to fetch data whenever proDta changes

  return (
    <Container>
      <Row>
        <div className="justify-content-md-center">
          <h2>
            <u>Product Details</u>
          </h2>
          {
            <center>
              {" "}
              <Card className="cards m-3" style={{ width: "50%" }}>
                <center>
                  <Card.Title>{data.name}</Card.Title>
                </center>
                <center>
                  <Card.Img
                    className="card-img"
                    variant="top"
                    style={{
                      width: "30%",
                    }}
                    src={data.image}
                  />
                </center>
                <Card.Body>
                  <Card.Text>
                    <Card.Text>
                      <p>{data.price}</p>
                    </Card.Text>
                  </Card.Text>
                </Card.Body>
              </Card>
            </center>
          }
        </div>
      </Row>
    </Container>
  );
}
export default StdDetails;
