import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";

import { Card, Col, Container, Row } from "react-bootstrap";
import axios from "axios";
function StdList() {
  const [data, setData] = useState([]);

  
  useEffect(() => {
    getrecord();
  }, []);

  const getrecord = () => {
    axios
      .get("http://localhost:5000/getdata")
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };


  return (
    <Container>
      <Row>
       
          <>
           {data.map((ele, i) => {
              return (
                <>
                  <Col
                    xs={12}
                    lg={4}
                    xl={3}
                    className=" col-xs-12 col-cards  mt-4"
                  >
                    <Card key={i} className="cards m-3">
                      <center>
                        <Card.Title>{ele.name}</Card.Title>
                      </center>
                      <center>
                        <Card.Img
                          className="card-img"
                          variant="top"
                          style={{
                            width: "50%",
                          }}
                          src={ele.image}
                        />
                      </center>
                      <Card.Body>
                        <Card.Text>
                          <Card.Text>
                            <p>{ele.price}</p>
                          </Card.Text>
                        </Card.Text>
                      </Card.Body>
                    </Card>
                  </Col>
                </>
              );
            })}
          </>

      </Row>
    </Container>
  );
}
export default StdList;
