import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import Table from "react-bootstrap/Table";
import Button from "react-bootstrap/Button";
import { Card, Col, Container, Row } from "react-bootstrap";
import axios from "axios";
function StdList() {
  const [data, setData] = useState([]);

  const navigate = useNavigate();
  useEffect(() => {
    getrecord();
  }, []);

  const getrecord = () => {
    axios
      .get("http://localhost:5000/getdata")
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  };

  const Edit = (id) => {
    navigate("/edit/" + id);
  };
  const Details = (id) => {
    navigate("/details/" + id);
  };

  const Delete = (id) => {
    axios
      .delete(`http://localhost:5000/deletedata/${id}`)
      .then(() => {
        getrecord();
      })
      .catch((error) => {
        console.error("Error deleting data:", error);
      });
  };
  return (
    <Container>
      <Row>
        <Col xs={12} lg={4} xl={3} className="col-xs-12 col-cards  mt-4">
          <>
            <h4>
              Add Student Data:
              <Link to="/create">
                <Button
                  className="mb-3 p-1"
                  as="input"
                  variant="info"
                  type="button"
                  value="Add"
                />
              </Link>
            </h4>
            <hr />

            {data.map((ele, id) => {
              return (
                <>
                  <Col
                    xs={12}
                    lg={4}
                    xl={3}
                    className=" col-xs-12 col-cards  mt-4"
                  >
                    <Card key={i} className="cards m-3">
                      <center>
                        <Card.Title>{ele.name}</Card.Title>
                      </center>
                      <center>
                        <Card.Img
                          className="card-img"
                          variant="top"
                          style={{
                            width: "50%",
                          }}
                          src={ele.image}
                        />
                      </center>
                      <Card.Body>
                        <Card.Text>
                          <Card.Text>
                            <p>{ele.price}</p>
                          </Card.Text>
                        </Card.Text>
                      </Card.Body>
                    </Card>
                  </Col>
                </>
              );
            })}
          </>
        </Col>
      </Row>
    </Container>
  );
}
export default StdList;
