import React, { useState, useEffect } from "react";
import { useParams, Link, useNavigate, Route } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Container } from "react-bootstrap";
function StdEdit() {
  const { stdDta } = useParams();
  const [data, Setdata] = useState({
    name: "",
    age: "",
    perc: "",
    active: true,
  });
  const handle = (e) => {
    Setdata({ ...data, [e.target.name]: e.target.value });
  };

  const navigate = useNavigate();

  const { name, age, perc, active } = data;
  useEffect(() => {
    getrecord();
  }, []);

  const getrecord = () => {
    fetch(`http://localhost:5000/data/${stdDta}`)
      .then((res) => res.json())
      .then((op) => Setdata(op));
  };
  const submitform = (e) => {
    e.preventDefault();
    const data = { name, age, perc, active };
    fetch(`http://localhost:5000/updatedata/${stdDta}`, {
      method: "PATCH",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    })
      .then((res) => res.json())
      .then((opt) => {
        alert("Data modified Successfully ");
        navigate("/");
      });
  };
  return (
    <>
     <Container>
        <Row>
            
        </Row>
     </Container>
    </>
  );
}
export default StdEdit;
