import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import { Container, Row } from "react-bootstrap";
function StdCreate() {
  const [data, Setdata] = useState({
    name: "",
    age: "",
    perc: "",
    active: true,
  });
  
  const navigate = useNavigate();
  const { name, age, perc, active } = data;
  const submitform = (e) => {
    e.preventDefault();
    const data = { name, age, perc, active };
    fetch("http://localhost:5000/adddata", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(data),
    })
      .then((res) => res.json())
      .then((opt) => {
        alert("Successfully Added");
        navigate("/");
      });
  };
  const handle = (e) => {
    Setdata({ ...data, [e.target.name]: e.target.value });
  };
  return (
    <>
      <Container>
        <Row>
          <div className="justify-content-md-center">
            <h2>
              <u>Student Details</u>
            </h2>
            {
              <div className="detailspage">
                <h3>
                  Student Id:<span>{data._id}</span>
                </h3>
                <h3>
                  Student Name:<span>{data.name}</span>
                </h3>
                <h3>
                  Student Email:<span>{data.email}</span>
                </h3>
                <h3>
                  Student Age:<span>{data.age}</span>
                </h3>
                <h3>
                  Student Percentage:<span>{data.perc}</span>
                </h3>
                <Link to="/">
                  <Button variant="primary">{">>"}Back</Button>
                </Link>
              </div>
            }
          </div>
        </Row>
      </Container>
    </>
  );
}
export default StdCreate;
