import React, { useState } from "react";

const App = () => {
  const [data, setData] = useState([
    {
      id: 1,
      name: "vineesha",
    },
    {
      id: 2,
      name: "Bhavani",
    },
    {
      id: 3,
      name: "Sirisha",
    },
    {
      id: 4,
      name: "Samatha",
    },
  ]);

  const [list, setList] = useState("");
  const [editid, setEditId] = useState(null);
  const [editvalue, setEditValue] = useState("");

  const add = () => {
    setData([...data, { id: data.length + 1, name: list }]);
    setList("");
  };

  const del = (i) => {
    const updatedData = data.filter((item, index) => index !== i);
    setData(updatedData);
  };

  const edit = (id, name) => {
    setEditId(id);
    setEditValue(name);
  };

  const update = () => {
    if (editvalue.trim() !== "") {
      const updatedData = data.map((item) =>
        item.id === editid ? { ...item, name: editvalue } : item
      );
      setData(updatedData);
      setEditId(null);
      setEditValue("");
    }
  };

  return (
    <div>
      <input
        type="text"
        value={list}
        onChange={(e) => setList(e.target.value)}
      />
      <button onClick={add}>Add</button>
      {data.map((dta, index) => (
        <div key={dta.id}>
          <h2>{dta.name}</h2>
          {editid === dta.id ? (
            <>
              <input
                type="text"
                value={editvalue}
                onChange={(e) => setEditValue(e.target.value)}
              />
              <button onClick={update}>Update</button>
            </>
          ) : (
            <button onClick={() => edit(dta.id, dta.name)}>Edit</button>
          )}
          <button onClick={() => del(index)}>Delete</button>
        </div>
      ))}
    </div>
  );
};

export default App;
