import { useState }  from  'react'


const App = () => {
   const [data, setdata] = useState([
     {
       id: 1,
       name: "vineesha",
     },
     {
       id: 2,
       name: "Bhavani",
     },
     {
       id: 3,
       name: "Sirisha",
     },
     {
       id: 4,
       name: "Samatha",
     },
   ]);

   const [list,setList]=useState("")
   const [editid,setEditid]=useState(null)
   const [editname,setEditname]=useState("")

   const add=()=>{
   setdata([...data,{name:list}])
   }
   const del=(idp1)=>{
const deldta=data.filter((item,idp)=>idp!=idp1)
setdata(deldta)
   }

   const edit=(id,name)=>{
    setEditid(id)
    setEditname(name)
   }
   const update=()=>{
    const updatedta=data.map((item)=>
    item.id==editid?{...item,name:editname}:item
    )
    setdata(updatedta)
    setEditid(null)
    setEditname("")
   }
  return (
    <div>
      <input
        type="text"
        value={editid !== null ? setList : setEditname}
        onChange={(e) => {
          editid !== null ? setEditname(e.target.value) : setList(e.target.value);
        }}
      />
      {editid !== null ? (
        <button onClick={update}>update</button>
      ) : (
        <button onClick={add}>ADD</button>
      )}
      {data.map((dta, i) => {
        return (
          <>
            <h5>{dta.name}</h5>
            <button onClick={() => del(i)}>Delete</button>
            <button onClick={() => edit(dta.id, dta.name)}>edit</button>
          </>
        );
      })}
    </div>
  );
}

export default App
