import React, { useState } from "react";

const App = () => {
  const [data, setData] = useState([
    {
      id: 1,
      name: "vineesha",
    },
    {
      id: 2,
      name: "Bhavani",
    },
    {
      id: 3,
      name: "Sirisha",
    },
    {
      id: 4,
      name: "Samatha",
    },
  ]);
  const [list, setList] = useState("");
  const [editId, setEditId] = useState(null);
  const [editValue, setEditValue] = useState("");

  const add = () => {
    if (list.trim() !== "") {
      const newItem = {
        id: Date.now(), // Unique ID for new item
        name: list,
      };
      setData([...data, newItem]);
      setList(""); // Clear input field after adding
    }
  };

  const del = (id) => {
    const updatedData = data.filter((item) => item.id !== id);
    setData(updatedData);
  };

  const edit = (id, name) => {
    setEditValue(name);
    setEditId(id);
  };

  const update = () => {
    if (editValue.trim() !== "") {
      const updatedData = data.map((item) =>
        item.id === editId ? { ...item, name: editValue } : item
      );
      setData(updatedData);
      setEditId(null);
      setEditValue("");
    }
  };

  return (
    <div>
      <input
        type="text"
        value={editId !== null ? editValue : list}
        onChange={(e) => {
          editId !== null
            ? setEditValue(e.target.value)
            : setList(e.target.value);
        }}
      />
      {editId !== null ? (
        <button onClick={update}>Update</button>
      ) : (
        <button onClick={add}>Add</button>
      )}
      {data.map((dta) => (
        <div key={dta.id}>
          <h2>{dta.name}</h2>
          <button onClick={() => del(dta.id)}>Delete</button>
          <button onClick={() => edit(dta.id, dta.name)}>Edit</button>
        </div>
      ))}
    </div>
  );
};

export default App;
