import React, { useState } from 'react'

const App = () => {
  const [data, setdata] = useState([
    {
      id: 1,
      name: "vineesha",
    },
    {
      id: 2,
      name: "Bhavani",
    },
    {
      id: 3,
      name: "Sirisha",
    },
    {
      id: 4,
      name: "Samatha",
    },
  ]);
  const [list, setList] = useState("");
const [editid,setedtitid]=useState(null)
const [editvalue,seteditvalue]=useState("")
const add=()=>{
  setdata([...data,{name:list}])
  setList("");
}

const del=(i)=>{
const updatedta=data.filter((item)=>item.id!=i)
setdata(updatedta)

}
const edit=(id,name)=>{
  seteditvalue(name)
  setedtitid(id)
}
const update=()=>{
  if(editvalue.trim()!==""){
   const updatedta=data.map((item)=>
   item.id==editid?{...item,name:editvalue}:list
   )
 setdata(updatedta);
  setedtitid(null);
  }
 
 
}
  return (
    <div>
      <input
        type="text"
        value={list}
        onChange={(e) => {
          editid !== null
            ? seteditvalue(e.target.value)
            : setList(e.target.value);
        }}
      />
      {editid !== null ? (
        <button onClick={update}>Update</button>
      ) : (
        <button onClick={add}>Add</button>
      )}
      {data.map((dta, id) => {
        return (
          <>
            <h2>{dta.name}</h2>
            <button onClick={() => del(dta.id)}>Delete</button>
            <button onClick={() => edit(dta.id, dta.name)}>Delete</button>
          </>
        );
      })}
    </div>
  );
}

export default App
