import React, { useState } from 'react';
import './style.css';

export default function App() {
  const [data, setData] = useState([
    {
      id: 1,
      name: "vinnu",
    },
    {
      id: 2,
      name: "Babbi",
    },
    {
      id: 3,
      name: "Sam",
    },
  ]);
  const [list,setList]=useState("")
  const [editid,setedtid]=useState(null)
  const [editvalue,setEditvalue]=useState("")



  const add=(e)=>{
   setData([...data,{name:list}])
  }
  const del=(i)=>{
    const updatedData=data.filter((item)=>item.id!==i)
    setData(updatedData)
  }


  const edit=(id,name)=>{
    setedtid(id)
    setEditvalue(name)
  }

  const update=()=>{

  }
  return (
    <div>
      <h1>Hello StackBlitz!</h1>
      <input type="text" onChange={(e) => setList(e.target.value)} />
      {editid == null ? (
        <button onClick={add}>ADD</button>
      ) : (
        <button onClick={add}>ADD</button>
      )}
      {data.map((dta, i) => {
        return (
          <>
            <h3>Name:{dta.name}</h3>
            <button onClick={() => del(dta.id)}>Delete</button>
            <button onClick={() => edit(dta.id, dta.name)}>Edit</button>
          </>
        );
      })}
    </div>
  );
}
