import React, { useState } from "react";
import "./style.css";
function FontChanger() {
  const [name, setName] = useState("ReactJS");
  const [originalName] = useState("Your Name"); // Store the original name
  const [fontSize, setFontSize] = useState("24px");
  const [fontStyle, setFontStyle] = useState("Arial, sans-serif");
  const [inputName, setInputName] = useState("");

  const handleFontSizeChange = (event) => {
    setFontSize(event.target.value);
  };

  const handleFontStyleChange = (event) => {
    setFontStyle(event.target.value);
  };

  const handleInputChange = (event) => {
    setInputName(event.target.value);
  };

  const handleAddButtonClick = () => {
    setName(inputName);
    setInputName("");
  };

  const handleResetButtonClick = () => {
    setName(originalName); // Reset the name to the original text
    setInputName(""); // Clear the input field
  };

  return (
    <div className="container">
      <h1 style={{ fontSize: fontSize, fontFamily: fontStyle }}>{name}</h1>
      <hr />
      <div className="options">
        <label htmlFor="fontSize">Select Font Size:</label>
        <br />
        <select
          id="fontSize"
          className="select"
          onChange={handleFontSizeChange}
          value={fontSize}
        >
          <option value="16px">Small</option>
          <option value="24px">Medium</option>
          <option value="32px">Large</option>
        </select>
        <br />
        <label htmlFor="fontStyle">Select Font Style:</label>
        <br />
        <select
          id="fontStyle"
          className="select"
          onChange={handleFontStyleChange}
          value={fontStyle}
        >
          <option value="Arial, sans-serif">Arial</option>
          <option value="Times New Roman, serif">Times New Roman</option>
          <option value="Verdana, sans-serif">Verdana</option>
          <option value="Courier New, monospace">Courier New</option>
        </select>
        <br />
        <input
          type="text"
          placeholder="Enter Name"
          value={inputName}
          onChange={handleInputChange}
        />
        <button onClick={handleAddButtonClick}>Add</button>
        <button onClick={handleResetButtonClick}>Reset</button>
      </div>
    </div>
  );
}

export default FontChanger;
