import React,{useState} from 'react'
import './style.css';

export default function App() {
  const [data, setdata] = useState([
    {
      id: 1,
      name: "vineesha"
    },
    {
      id: 2,
      name: "Bhavani"
    },
    {
      id: 3,
      name: "Sirisha"
    },
    {
      id: 4,
      name: "Samatha"
    },
  ]);
const [list,setList]=useState("")
const [editid,setEditid]=useState(null)
const [editvalue,setEditvalue]=useState("")

const add=(e)=>{
  e.preventDefault();
  setdata([...data,{name:list}])
  setList("")
}
const edit=(id,name)=>{
setEditid(id)
setEditvalue(name)
}

const remove=(id)=>{
const update=data.filter((item=>item.id!==id))
setdata(update)
}
const update=()=>{
 if(editvalue.trim()!==""){
const update=data.map((item=>
  item.id==editid?{...item,name:editvalue}:item))
  setdata(update)
  setEditid(null)
  setEditvalue("")
 }
}
  return (
    <div>
      <input type="text" value={editid!==null?editvalue:list}
      onChange={(e)=>{editid!==null?setEditvalue(e.target.value):setList(e.target.value)}}/>
    {
      editid!==null?  <button onClick={update}>UPDATE</button>:
      <button onClick={add}>ADD</button>
    }

{
  data.map((res)=>{
    return(
      <div>

        <h2>{res.name}</h2>
        <button onClick={()=>remove(res.id)}>REMOVE</button>
        <button onClick={()=>edit(res.id, res.name)}>EDIT</button>
      </div>
    )
  })
}
    </div>
  );
}
