import React, { useState } from "react";

const App = () => {
  const [data, setdata] = useState([
    {
      id: 1,
      name: "vineesha",
      editing: false, // Add an 'editing' property to track editing state for each item
    },
    {
      id: 2,
      name: "Bhavani",
      editing: false,
    },
    {
      id: 3,
      name: "Sirisha",
      editing: false,
    },
    {
      id: 4,
      name: "Samatha",
      editing: false,
    },
  ]);

  const [list, setList] = useState("");

  const add = () => {
    if (list.trim() !== "") {
      const newItem = {
        id: data.length + 1,
        name: list,
        editing: false,
      };
      setdata([...data, newItem]);
      setList("");
    }
  };

  const del = (id) => {
    const updatedData = data.filter((item) => item.id !== id);
    setdata(updatedData);
  };

  const edit = (id) => {
    const updatedData = data.map((item) =>
      item.id === id ? { ...item, editing: true } : { ...item, editing: false }
    );
    setdata(updatedData);
  };

  const update = (id, newName) => {
    const updatedData = data.map((item) =>
      item.id === id ? { ...item, name: newName, editing: false } : item
    );
    setdata(updatedData);
  };

  return (
    <div>
      <input
        type="text"
        value={list}
        onChange={(e) => setList(e.target.value)}
      />
      <button onClick={add}>Add</button>
      {data.map((dta) => (
        <div key={dta.id}>
          {dta.editing ? (
            <>
              <input
                type="text"
                value={dta.name}
                onChange={(e) => update(dta.id, e.target.value)}
              />
              <button onClick={() => update(dta.id, dta.name)}>Update</button>
            </>
          ) : (
            <>
              <h2>{dta.name}</h2>
              <button onClick={() => del(dta.id)}>Delete</button>
              <button onClick={() => edit(dta.id)}>Edit</button>
            </>
          )}
        </div>
      ))}
    </div>
  );
};

export default App;
