import React, { useState } from "react";
import "./style.css";
function Tododata() {
  const [data, setdata] = useState([
    {
      id: 1,
      name: "vineesha",
    },
    {
      id: 2,
      name: "Bhavani",
    },
    {
      id: 3,
      name: "Sirisha",
    },
    {
      id: 4,
      name: "Samatha",
    },
  ]);

  const [formdata, setFormdata] = useState({ name: "" });

  const handlechange = (e) => {
    setFormdata({ ...formdata, [e.target.name]: e.target.value });
  };

  const add = (e) => {
    e.preventDefault();
    setdata([...data, { name: formdata.name }]);
    setFormdata({ name: "" });
  };

  const handleDelete = (ind) => {
    const updatedData = data.filter((item, i) => i !== ind);
    setdata(updatedData);
  };

  return (
    <div className="todoapp">
      <center>
        <h3>Todo data:</h3>
        <input
          value={formdata.name}
          name="name"
          type="text"
          onChange={handlechange}
        />
        <button onClick={add}>ADD</button>
        <hr />

        <table>
          {data.map((res, ind) => {
            return (
              <>
                <tr key={ind}>
                  <td>{ind + 1}.</td>
                  <td> {res.name} </td>
                  <td>
                    {" "}
                    <button onClick={() => handleDelete(ind)}>Delete</button>
                  </td>
                </tr>
              </>
            );
          })}
        </table>
      </center>
    </div>
  );
}
export default Tododata;
