import React, { useState } from "react";

const App = () => {
  const [data, setdata] = useState([
    {
      id: 1,
      name: "vineesha",
    },
    {
      id: 2,
      name: "Bhavani",
    },
    {
      id: 3,
      name: "Sirisha",
    },
    {
      id: 4,
      name: "Samatha",
    },
  ]);
  const [list, setList] = useState("");
  const [editid, setedtitid] = useState(null);
  const [editvalue, seteditvalue] = useState("");
  const add = () => {
    setdata([...data, { name: list }]);
  
  };

  const del = (i) => {
    const deldta = data.filter((item,ind) => ind !== i);
    setdata(deldta);
  };
  const edit = (id, name) => {
    seteditvalue(name);
    setedtitid(id);
  };
 const update = () => {
   if (editvalue.trim() !== "") {
     const update = data.map((item) =>
       item.id == editid ? { ...item, name: editvalue } : item
     );
     setdata(update);
     set(null);
     setEditvalue("");
   }
 };
  return (
    <div>
      <input
        type="text"
        value={editid !== null ? editvalue : list}
        onChange={(e) => {
          editid !== null
            ? seteditvalue(e.target.value)
            : setList(e.target.value);
        }}
      />
      {editid !== null ? 
        <button onClick={update}>Update</button>
      : 
        <button onClick={add}>Add</button>
      }
      {data.map((dta, ind) => {
        return (
          <>
            <h2>{dta.name}</h2>
            <button onClick={() => del(ind)}>Delete</button>
            <button onClick={() => edit(dta.id, dta.name)}>Edit</button>
          </>
        );
      })}
    </div>
  );
};

export default App;
