import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import Button from "react-bootstrap/Button";
import { Card, Container, Row } from "react-bootstrap";
import axios from "axios";
function StdDetails() {
  const [data, setData] = useState({});
  const { proDta } = useParams();
  useEffect(() => {
    axios
      .get(`http://localhost:5000/data/${proDta}`)
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
      });
  }, [proDta]); // Include proDta in the dependency array if you want to fetch data whenever proDta changes

  return (
    <Container>
      <Row>
        <div className="justify-content-md-center">
          <h2>
            <u>Product Details</u>
          </h2>
          {
            <Card key={i} className="cards m-3">
              <center>
                <Card.Title>{ele.name}</Card.Title>
              </center>
              <center>
                <Card.Img
                  className="card-img"
                  variant="top"
                  style={{
                    width: "50%",
                  }}
                  onClick={() => Details(ele._id)}
                  src={ele.image}
                />
              </center>
              <Card.Body>
                <Card.Text>
                  <Card.Text>
                    <p>{ele.price}</p>
                  </Card.Text>
                </Card.Text>
              </Card.Body>
            </Card>
          }
        </div>
      </Row>
    </Container>
  );
}
export default StdDetails;
