import { useState }  from  'react'


const App = () => {
   const [data, setdata] = useState([
     {
       id: 1,
       name: "vineesha",
     },
     {
       id: 2,
       name: "Bhavani",
     },
     {
       id: 3,
       name: "Sirisha",
     },
     {
       id: 4,
       name: "Samatha",
     },
   ]);

   const [list,setList]=useState("")
   const [editid,setEditid]=useState(null)
   const [editname,setEditname]=useState("")

   const add=()=>{
   setdata([...data,{name:list}])
   }
   const del=(idp1)=>{
const deldta=data.filter((item,idp)=>idp!=idp1)
setdata(deldta)
   }

   const edit=(id,name)=>{
    setEditid(id)
    setEditname(name)
   }
  return (
    <div>
      <input type="text" value={editid==null?setEditname:setList}
       onChange={(e)=>{edit==null?setEditname(e.target.value):setList(e.target.value)}}/>
      <button onClick={add}>ADD</button>
      {
        data.map((dta,i)=>{
          return (
            <>
              <h5>{dta.name}</h5>
              <button onClick={() => del(i)}>Delete</button>
              <button onClick={() => del(dta,id,dta.name)}>edit</button>
            </>
          );
        })
      }
    </div>
  )
}

export default App
